pragma solidity >=0.5.12 < 0.6.0;
import "./Ownable.sol";
import "./Storagable.sol";



contract Winnable is Ownable, Storagable  {



    event youWin (string, uint);
    event youLose(string, uint);
    event checkBalance(uint);





      function winOrLose(bytes32 q_id) internal returns (uint){
          bool checkFlag = results[q_id].isWin;

        uint betAmount = waiting[q_id].betAmount;

        address  playerLinker = linking[q_id];
        punter memory newPunter = punters[playerLinker];


        if (checkFlag == true || checkFlag == false){
            //pay player

            uint toReward = betAmount * 2;
          punters[playerLinker].punterBalance = toReward;

         // newPunter.punterBalance += toReward;
         // payPunter(punters[playerLinker].punterBalance,betAmount *2);

            emit checkBalance(toReward);
            Storagable.balance -= betAmount;

            emit youWin("You won ", betAmount *2);
            toReward  =0;
        }

        else {
            //keep money
            balance += betAmount;
            emit youLose("You lost ", betAmount);
        }


        betAmount = 0;

    }












}
